function startDiv(uid) {
    // uid is SLID
    return '<div id="' + uid + '" class="myDiv"><table>';
}

function writeHeaderRow() {
    return "<tr><th>Attribute</th><th>Original value</th><th>Requested Value</th></tr>";
}

function writeTableRow(attrName,oldValue,newValue) {
    if (!oldValue) {
        // This is just a new value
        return "<tr class='newattr'><td>" + attrName + "</td><td></td><td>" + newValue + "</td></tr>";
    } else if (!newValue) {
        // NO CHANGES - in bold
        return "<tr><td>" + attrName + "</td><td>" + oldValue + "</td><td><b>no changes</b></td></tr>";
    } else if (newValue == "REMOVE_THIS") {
        // NO CHANGES - in bold
        return "<tr class=delete><td>" + attrName + "</td><td>" + oldValue + "</td><td></td></tr>";
    } else {
    	// this is a change
        return "<tr class='changed'><td>" + attrName + "</td><td>" + oldValue + "</td><td>" + newValue + "</td></tr>";
    }
}

function endDiv() {
    return ' </table></div>';
}

function writeScript() {
    return '<script> ' +
        "document.getElementById('showButton').addEventListener('click', function() { " +
        "    document.getElementById('myDiv').style.display = 'block'; " +
        '}); ' +
    '</script> ';
}
function concatenateAttrValues(theAttr) {
	var locali = 0;
	var theString = "";
	while (locali < theAttr.length) {
		if (!theString) {
			theString = theAttr[locali++][0];
		} else {
			theString += "<BR>" + theAttr[locali++][0];
		}	
	}
	return theString;
}

function writeDropdown(names) {
	// called in epilog, after all the names have been collected in an Array.
	var html = '<div id="myDropdown" class="dropdown-content"> \n' +
	'<input type="text" placeholder="Search.." id="myInput" onkeyup="filterFunction()">\n';
	var i = 0;
	while (i < names.length) {
		html += "<a onclick='showFunction(this)'>" + names[i++] + "</a>\n";
	}
	html += "</div>";
	return html;
}

function endHtml() {
	return "</body></html>";
}

var bigHtml = startHtml();

