while (i < myEntry.Object.Attribute.length) {
	localAttr = myEntry.Object.Attribute[i++];
	if (localAttr["@name"] != "AttributeChangeOperation") {
		// not sure why, but sometimes listing existing attributes, one or more may have a null
		// value, which means they have no "Scalar" element in XML.
		if (localAttr["Scalar"]) {
			if (localAttr["Scalar"].length) {
				myEntry.setAttribute(localAttr["@name"],concatenateAttrValues(localAttr["Scalar"]));
			} else {
				myEntry.setAttribute(localAttr["@name"],localAttr["Scalar"][0]);
				if (localAttr["@name"] == "uid") {
					listOfNames.push(localAttr["Scalar"][0] + " " + work.getString("SCHEDULED"));
					task.logmsg("current uid = " + localAttr["Scalar"])
				}
			}
		} else {
			myEntry.setAttribute(localAttr["@name"],"");
		}		
		myEntry[localAttr["@name"]].addValue("");
		//work[localAttr["@name"]].addValue("");
	} else {
		// inside one of the "change" attributes
		var j = 0;
		var theLength = 0;
		var onlyOne = false;
		var oper = localAttr.Object["@name"].toString().replace(/.*\./,"");
		if (localAttr.Object.Attribute.length) {
			// there's more than one attribute
			theLength = localAttr.Object.Attribute.length;
		} else {
			// there's only one attribue, so manually set theLength to 1
			theLength = 1;
			onlyOne = true;
		}
		while (j < theLength) {
			if (!onlyOne) {
				veryLocalAttr = localAttr.Object.Attribute[j];
			} else {
				veryLocalAttr = localAttr.Object.Attribute;
			}	
			if (myEntry[veryLocalAttr["@name"]]) {
				if (oper == "replace") {
					if (veryLocalAttr["Scalar"].length) {
						myEntry[veryLocalAttr["@name"]].setValue(1,concatenateAttrValues(veryLocalAttr["Scalar"]));
					} else {
						myEntry[veryLocalAttr["@name"]].setValue(1,veryLocalAttr["Scalar"][0]);
					}	

				} else if (oper == "remove") {
					myEntry[veryLocalAttr["@name"]].setValue(1,"REMOVE_THIS");
					//don't need to do anything - we already set this to ""
				} else if (oper == "add") { // this is an add  
					// need to loop through this probably.
					myEntry.setAttribute(veryLocalAttr["@name"],"");
					if (veryLocalAttr["Scalar"].length) {
						myEntry[veryLocalAttr["@name"]].setValue(1,concatenateAttrValues(veryLocalAttr["Scalar"]));
					} else {
						myEntry[veryLocalAttr["@name"]].setValue(1,veryLocalAttr["Scalar"][0]);
					}	

				}
			}	
			j=j+1;
		}

	}
}
function sortFunction(a, b) {
    var lowerA = a.toLowerCase();
    var lowerB = b.toLowerCase();

    if (lowerA < lowerB) {
        return -1;
    }
    if (lowerA > lowerB) {
        return 1;
    }
    return 0;
}

var sortedAttrList = myEntry.getAttributeNames().sort(sortFunction); // gives me an array of attribute names in alphabetical order.
/*
 * So now, we just need to iterate through the elements of sortedAttrList to create a table
 * that is in alphabetical order.
 */
var k = 0;
var localHtml = startDiv(myEntry.getString("uid") + " " + work.getString("SCHEDULED"));
localHtml += writeHeaderRow();
while (k < sortedAttrList.length) {
	localHtml += writeTableRow(sortedAttrList[k],myEntry.getAttribute(sortedAttrList[k]).getValue(0),myEntry.getAttribute(sortedAttrList[k]).getValue(1));
	k++;
}
localHtml += endDiv();
